[R49] {'_type': 'review', 'review_id': '3ddvXbeP8TC_5Bfq2zeL5A', 'user_id': 'CHg2dz_PohXVVV4bIWjNOQ', 'business_id': '_C7QiQQc47AOEv4PE3Kong', 'stars': 5.0, 'useful': 0, 'funny': 0, 'cool': 0, 'text': "This was hands down WORTH the hype. 

