[R17] {'review_id': '9qJBnJHyN3vh-b7daT5q9w', 'user_id': 'OWHZDW2q3sfgbQeJiajO1g', 'business_id': 'YBjFuFJXKBKdsxx2TlQVPw', 'stars': 5.0, 'useful': 1, 'funny': 0, 'cool': 0, 'text': 'Best airport pizza I have ever had. The crust was crispy on the outside and soft on the inside. The toppings tasted fresh, and the service was polite and very friendly.  The employees all seemed to be enjoying themselves.', 'date': '2019-09-19 17:12:15', 'user': {'name': 'Daniel', 'review_count': 63, 'yelping_since': '2013-04-10 23:00:25', 'elite': '', 'average_stars': 3.27, 'fans': 3}}
